CREATE TABLE news(id INT IDENTITY PRIMARY KEY NOT NULL, title VARCHAR NOT NULL, content VARCHAR NULL);
CREATE TABLE user(id INT IDENTITY PRIMARY KEY NOT NULL, username VARCHAR NOT NULL, password VARCHAR NOT NULL);
CREATE TABLE category(id INT IDENTITY PRIMARY KEY NOT NULL, name VARCHAR NOT NULL);
CREATE TABLE challenge(id INT IDENTITY PRIMARY KEY NOT NULL, category_id INT NOT NULL, name VARCHAR NOT NULL, content VARCHAR  NULL);
CREATE TABLE hint(id INT IDENTITY PRIMARY KEY NOT NULL, challenge_id INT NOT NULL, title VARCHAR NOT NULL, content VARCHAR  NULL);
CREATE TABLE attachfile(id INT IDENTITY PRIMARY KEY NOT NULL, challenge_id INT NOT NULL, filename VARCHAR NOT NULL, filelocation VARCHAR  NULL, uploadtime TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP);
CREATE TABLE team(id INT IDENTITY PRIMARY KEY NOT NULL, name VARCHAR NOT NULL, score INT NULL);
CREATE TABLE rule(id INT IDENTITY PRIMARY KEY NOT NULL, name VARCHAR NULL, content VARCHAR NULL);
CREATE TABLE operationlog(id INT IDENTITY PRIMARY KEY NOT NULL, user_id INT NOT NULL, ipaddress VARCHAR NOT NULL, description VARCHAR NULL, operationtime TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP);
CREATE TABLE submission(id INT IDENTITY PRIMARY KEY NOT NULL, user_id INT NOT NULL, challenge_id INT NOT NULL, content VARCHAR NOT NULL, correct BOOL NULL, submittime TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP);
